package clique

import (
	// "math/big"

	"github.com/ethereum/go-ethereum/accounts/abi"
	"github.com/ethereum/go-ethereum/common"
)

type systemContract struct {
	address    common.Address
	deployedBytecode    []byte
	packFun func() ([]byte, error)
}

func getSystemContracts(abi map[string]abi.ABI, initCommittees []common.Address, admin common.Address) []*systemContract {

	return []*systemContract{
		{
			// Committee Contract
			address: committeeAddress,
			deployedBytecode: committeeCode,
			packFun: func() ([]byte, error) {
				return abi[committeeContractName].Pack("initialize",
					initCommittees,
					admin,
				)
			},
		},
	}
}

var committeeCode = common.FromHex("")